// ---------- Leads / Estimates (with photo queue) ----------
async function processQueue(token){
  return new Promise(resolve => {
    db.transaction(tx => {
      tx.executeSql('SELECT * FROM queue', [], async (_, { rows }) => {
        const items = rows._array || [];
        for(const q of items){
          try{
            const payload = JSON.parse(q.payload);
            if(q.type === 'createLead'){ await api('/leads', 'POST', payload, token); }
            if(q.type === 'uploadPhoto'){ await api('/upload/image', 'POST', { dataUrl: payload.dataUrl }, token); }
            db.transaction(txx => txx.executeSql('DELETE FROM queue WHERE id=?', [q.id]));
          }catch(e){ /* keep for later */ }
        }
        resolve();
      });
    });
  });
}

function LeadsScreen({ navigation }){
  const { token } = useAuth();
  const [leads, setLeads] = useState([]);
  const [refreshing, setRefreshing] = useState(false);
  const fetchLeads = async () => {
    try {
      const data = await api('/leads', 'GET', null, token);
      setLeads(data);
      db.transaction(tx => {
        tx.executeSql('DELETE FROM leads_cache;');
        data.forEach(l => tx.executeSql('INSERT OR REPLACE INTO leads_cache (id, description, status) VALUES (?,?,?)', [l.id, l.description||'', l.status||'NEW']));
      });
    } catch(e){
      db.transaction(tx => tx.executeSql('SELECT id, description, status FROM leads_cache', [], (_, { rows }) => setLeads(rows._array || [])));
    }
  };
  useEffect(() => { fetchLeads(); }, []);
  const onRefresh = async () => {
    setRefreshing(true);
    await fetchLeads();
    setRefreshing(false);
  };
  return (
    <SafeAreaView style={{ flex:1 }}>
      <FlatList
        data={leads}
        keyExtractor={item => String(item.id)}
        contentContainerStyle={{ padding:12 }}
        refreshControl={<RefreshControl refreshing={refreshing} onRefresh={onRefresh} />}
        renderItem={({ item }) => (
          <TouchableOpacity style={{ padding:12, borderWidth:1, marginBottom:8 }} onPress={() => navigation.navigate('EstimateEditor', { leadId: item.id })}>
            <Text style={{ fontWeight:'600' }}>Lead #{item.id}</Text>
            <Text numberOfLines={2}>{item.description || 'No description'}</Text>
            <Text>Status: {item.status}</Text>
          </TouchableOpacity>
        )}
        ListHeaderComponent={<Button title="New Lead" onPress={()=>navigation.navigate('NewLead')} />}
      />
    </SafeAreaView>
  );
}

