function EstimateEditorScreen({ route, navigation }){
  const { token, user } = useAuth();
  const { leadId } = route.params || {};
  const [estimateId, setEstimateId] = useState(null);
  const [items, setItems] = useState([]);
  const [desc, setDesc] = useState('');
  const [qty, setQty] = useState('1');
  const [price, setPrice] = useState('0');
  const [notes, setNotes] = useState('');
  const [photoUrl, setPhotoUrl] = useState(null);
  const [subtotal, setSubtotal] = useState(0);
  const [taxRate, setTaxRate] = useState(7.0);
  const [tax, setTax] = useState(0);
  const [total, setTotal] = useState(0);

  const createEstimate = async () => {
    const est = await api('/estimates', 'POST', { leadId, subtotal:0, taxRate:7.0, total:0 }, token);
    setEstimateId(est.id);
  };

  const addItem = async () => {
    if(!estimateId){ alert('Create estimate first'); return; }
    const data = await api(`/estimates/${estimateId}/items`, 'POST', { description: desc, qty: parseFloat(qty), unitPrice: parseFloat(price) }, token);
    setItems(prev => [data, ...prev]); setDesc(''); setQty('1'); setPrice('0');
  };

  const suggest = async () => {
    const r = await api('/ai/estimate-suggest', 'POST', { notes }, token);
    if(!estimateId) await createEstimate();
    for(const it of r.items){ await api(`/estimates/${estimateId}/items`, 'POST', it, token); }
    Alert.alert('Added', 'Suggested line-items added');
  };

  const pickPhoto = async () => {
    const res = await ImagePicker.launchImageLibraryAsync({ base64: true, quality: 0.6 });
    if(!res.canceled && res.assets && res.assets[0].base64){
      const dataUrl = `data:image/jpeg;base64,${res.assets[0].base64}`;
      try {
        const up = await api('/upload/image', 'POST', { dataUrl }, token);
        setPhotoUrl(up.url);
      } catch(e){
        db.transaction(tx => tx.executeSql('INSERT INTO queue (type, payload) VALUES (?,?)', ['uploadPhoto', JSON.stringify({ dataUrl })]));
        Alert.alert('Queued upload', 'Will send when online');
      }
    }
  };

  const approveAndSign = () => { if(!estimateId){ Alert.alert('Create estimate'); return; } navigation.navigate('Signature', { estimateId }); };

  const canEstimate = user?.role === 'ADMIN' || user?.role === 'ESTIMATOR';

  useEffect(()=>{
    const s = items.reduce((acc, it) => acc + (parseFloat(it.qty||0) * parseFloat(it.unitPrice||0)), 0);
    const t = s * (parseFloat(taxRate||0)/100);
    setSubtotal(s);
    setTax(t);
    setTotal(s + t);
  }, [items, taxRate]);

  return (
    <SafeAreaView style={{ flex:1, padding:16 }}>
      <Text style={{ fontSize:18, marginBottom:8 }}>Estimate (Lead #{leadId})</Text>
      {canEstimate ? <Button title={estimateId ? `Estimate #${estimateId}` : "Create Estimate"} onPress={createEstimate} /> : <Text>View only (insufficient role)</Text>}
      <View style={{ height:12 }} />
      <TextInput placeholder="Item description" value={desc} onChangeText={setDesc} style={{ borderWidth:1, padding:8, marginBottom:6 }} />
      <TextInput placeholder="Qty" value={qty} onChangeText={setQty} keyboardType='decimal-pad' style={{ borderWidth:1, padding:8, marginBottom:6 }} />
      <TextInput placeholder="Unit Price" value={price} onChangeText={setPrice} keyboardType='decimal-pad' style={{ borderWidth:1, padding:8, marginBottom:6 }} />
      {canEstimate && <Button title="Add Item" onPress={addItem} />}
      <View style={{ height:16 }} />
      <Text style={{ fontWeight:'600' }}>AI helper</Text>
      <TextInput placeholder="Paste field notes..." value={notes} onChangeText={setNotes} multiline style={{ borderWidth:1, minHeight:80, padding:8, marginBottom:6 }} />
      {canEstimate && <Button title="Suggest Line Items" onPress={suggest} />}
      <View style={{ height:16 }} />
      <Text style={{ fontWeight:'600', marginBottom:6 }}>Totals</Text>
      <View style={{ flexDirection:'row', justifyContent:'space-between', marginBottom:6 }}>
        <Text>Subtotal</Text><Text>${subtotal.toFixed(2)}</Text>
      </View>
      <View style={{ flexDirection:'row', alignItems:'center', justifyContent:'space-between', marginBottom:6 }}>
        <Text>Tax Rate (%)</Text>
        <TextInput value={String(taxRate)} onChangeText={(v)=>setTaxRate(parseFloat(v)||0)} keyboardType='decimal-pad' style={{ borderWidth:1, padding:6, width:80, textAlign:'right' }} />
      </View>
      <View style={{ flexDirection:'row', justifyContent:'space-between', marginBottom:6 }}>
        <Text>Tax</Text><Text>${tax.toFixed(2)}</Text>
      </View>
      <View style={{ flexDirection:'row', justifyContent:'space-between', marginBottom:12 }}>
        <Text style={{ fontWeight:'700' }}>Total</Text><Text style={{ fontWeight:'700' }}>${total.toFixed(2)}</Text>
      </View>
      <View style={{ height:8 }} />
      <Button title="Attach Photo" onPress={pickPhoto} />
      {photoUrl ? <Image source={{ uri: `${API_URL}${photoUrl}` }} style={{ height:120, marginTop:8 }} /> : null}
      <View style={{ height:16 }} />
      <Button title="Approve & Sign" onPress={approveAndSign} />
      <View style={{ height:8 }} />
      <Button title="Send Estimate" onPress={async ()=>{
        if(!estimateId) return Alert.alert('Create estimate first');
        try { await api(`/estimates/${estimateId}/send`, 'POST', {}, token); Alert.alert('Sent', 'Estimate sent'); } catch(e){ Alert.alert('Error', e.message); }
      }} />
      <View style={{ height:8 }} />
      <Button title="Download PDF" onPress={async ()=>{
        if(!estimateId) return Alert.alert('Create estimate first');
        const fileUri = `${FileSystem.documentDirectory}estimate-${estimateId}.pdf`;
        try {
          await FileSystem.downloadAsync(`${API_URL}/pdf/estimate/${estimateId}`, fileUri, { headers: { Authorization: `Bearer ${token}` } });
          Alert.alert('Saved', `PDF saved to:\n${fileUri}`);
        } catch (e) { Alert.alert('Error', e.message); }
      }} />
      <View style={{ height:16 }} />
      <FlatList
        data={items}
        keyExtractor={(i, idx)=> String(i.id || idx)}
        renderItem={({ item }) => (
          <View style={{ padding:10, borderWidth:1, marginBottom:8 }}>
            <Text style={{ fontWeight:'600' }}>{item.description}</Text>
            <Text>Qty: {item.qty} × ${item.unitPrice} = ${ (parseFloat(item.qty||0)*parseFloat(item.unitPrice||0)).toFixed(2) }</Text>
          </View>
        )}
      />
    </SafeAreaView>
  );
}

