async function processQueue(token) {
  return new Promise(resolve => {
    db.transaction(tx => {
      tx.executeSql('SELECT * FROM queue', [], async (_, {
        rows
      }) => {
        const items = rows._array || [];
        for (const q of items) {
          try {
            const payload = JSON.parse(q.payload);
            if (q.type === 'createLead') {
              await api('/leads', 'POST', payload, token);
            }
            if (q.type === 'uploadPhoto') {
              await api('/upload/image', 'POST', {
                dataUrl: payload.dataUrl
              }, token);
            }
            db.transaction(txx => txx.executeSql('DELETE FROM queue WHERE id=?', [q.id]));
          } catch (e) {}
        }
        resolve();
      });
    });
  });
}
