import React, { useEffect, useState, createContext, useContext, useRef, useCallback } from 'react';
import { NavigationContainer, useFocusEffect } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import {Text, TextInput, Button, FlatList, TouchableOpacity, SafeAreaView, Alert, Image, ScrollView, RefreshControl } from 'react-native';
import * as ImagePicker from 'expo-image-picker';
import * as FileSystem from 'expo-file-system';
import * as SQLite from 'expo-sqlite';
import Svg, { Path } from 'react-native-svg';
import ViewShot from 'react-native-view-shot';
import { Platform, View } from 'react-native';
import * as SecureStore from 'expo-secure-store';
// import * as SQLite from 'expo-sqlite';


const API_URL = process.env.EXPO_PUBLIC_API_URL || 'http://localhost:4000';
// const db = SQLite.openDatabase('offline.db');

// function initDB(){
//   db.transaction(tx => {
//     tx.executeSql('CREATE TABLE IF NOT EXISTS queue (id INTEGER PRIMARY KEY AUTOINCREMENT, type TEXT, payload TEXT);');
//     tx.executeSql('CREATE TABLE IF NOT EXISTS uploads (id INTEGER PRIMARY KEY AUTOINCREMENT, dataUrl TEXT);');
//     tx.executeSql('CREATE TABLE IF NOT EXISTS leads_cache (id INTEGER PRIMARY KEY, description TEXT, status TEXT);');
//   });
// }

function initOfflineTables(database){
  try {
    if (!database || typeof database.transaction !== 'function') return;
    database.transaction(tx => {
      tx.executeSql && tx.executeSql('CREATE TABLE IF NOT EXISTS queue (id INTEGER PRIMARY KEY AUTOINCREMENT, type TEXT, payload TEXT);');
      tx.executeSql && tx.executeSql('CREATE TABLE IF NOT EXISTS uploads (id INTEGER PRIMARY KEY AUTOINCREMENT, dataUrl TEXT);');
      tx.executeSql && tx.executeSql('CREATE TABLE IF NOT EXISTS leads_cache (id INTEGER PRIMARY KEY, description TEXT, status TEXT);');
      tx.executeSql && tx.executeSql('CREATE TABLE IF NOT EXISTS jobs_cache (id INTEGER PRIMARY KEY, name TEXT, status TEXT, startDate TEXT, endDate TEXT, notes TEXT);');
      tx.executeSql && tx.executeSql('CREATE TABLE IF NOT EXISTS tasks_cache (id INTEGER PRIMARY KEY, jobId INTEGER, title TEXT, status TEXT, dueDate TEXT);');
      tx.executeSql && tx.executeSql('CREATE TABLE IF NOT EXISTS calendar_cache (id INTEGER PRIMARY KEY, jobId INTEGER, title TEXT, startAt TEXT, endAt TEXT);');
      tx.executeSql && tx.executeSql('CREATE TABLE IF NOT EXISTS estimates_cache (id INTEGER PRIMARY KEY, leadId INTEGER, subtotal REAL, taxRate REAL, total REAL, status TEXT);');
      tx.executeSql && tx.executeSql('CREATE TABLE IF NOT EXISTS estimate_items_cache (id INTEGER PRIMARY KEY, estimateId INTEGER, description TEXT, qty REAL, unitPrice REAL);');
      tx.executeSql && tx.executeSql('CREATE TABLE IF NOT EXISTS change_orders_cache (id INTEGER PRIMARY KEY, jobId INTEGER, title TEXT, amountDelta REAL, status TEXT);');
      tx.executeSql && tx.executeSql('CREATE TABLE IF NOT EXISTS users_cache (id INTEGER PRIMARY KEY, email TEXT, fullName TEXT, role TEXT);');
    });
  } catch (e) {
    // best-effort; avoid noisy alerts in offline mode
  }
}

let db;
export function getDb() {
  if (db) return db;

  // Web: safe no-op DB
  if (Platform.OS === 'web') {
    db = {
      transaction: (fn) => fn({ executeSql: () => {} }),
      execAsync: async () => {},
      runAsync: async () => {},
    };
    return db;
  }

  // SDK 51+: prefer sync, then async; DO NOT reference openDatabase (removed)
  if ('openDatabaseSync' in SQLite) {
    db = SQLite.openDatabaseSync('offline.db');
    return db;
  }

  if ('openDatabaseAsync' in SQLite) {
    // Minimal adapter so existing code won't crash; refactor later if needed
    db = {
      transaction: (fn) => fn({ executeSql: () => {} }),
      execAsync: async () => {},
      runAsync: async () => {},
    };
    return db;
  }

  // Safety fallback
  db = {
    transaction: (fn) => fn({ executeSql: () => {} }),
    execAsync: async () => {},
    runAsync: async () => {},
  };
  return db;
}

// DB handle
const dbHandle = getDb();
initOfflineTables(dbHandle);

// ---------- Helpers ----------
const normalizeRole = (role) => {
  if (!role) return 'TECH';
  const r = String(role).toUpperCase();
  if (['ADMIN','ESTIMATOR','SUPERVISOR','TECH'].includes(r)) return r;
  if (r === 'STANDARD' || r === 'USER') return 'TECH';
  return r;
};

const AuthContext = createContext(null);
function useAuth(){ return useContext(AuthContext); }
async function api(path, method='GET', body, token){
  const res = await fetch(`${API_URL}${path}`, {
    method, headers: { 'Content-Type':'application/json', ...(token?{Authorization:`Bearer ${token}`}:{}) },
    body: body?JSON.stringify(body):undefined
  });
  if(!res.ok){ throw new Error((await res.json()).error || 'Request failed'); }
  return res.json();
}

// ---------- Signature Pad to PNG ----------
function SignaturePad({ onCapturePng }){
  const [points, setPoints] = useState([]);
  const [path, setPath] = useState('');
  const shotRef = useRef(null);
  return (
    <ViewShot ref={shotRef} options={{ format: 'png', quality: 0.9, result: 'base64' }} style={{ height:220, borderWidth:1, backgroundColor:'#fff' }}>
      <View
        onStartShouldSetResponder={()=>true}
        onResponderMove={(e)=>{
          const x = e.nativeEvent.locationX, y = e.nativeEvent.locationY;
          setPath(p => p + (p ? ` L ${x} ${y}` : ` M ${x} ${y}`));
        }}
        onResponderRelease={async ()=>{
          const base64 = await shotRef.current.capture();
          onCapturePng && onCapturePng('data:image/png;base64,' + base64);
        }}
        style={{ flex:1 }}
      >
        <Svg height="100%" width="100%"><Path d={path} stroke="black" strokeWidth="2" fill="none" /></Svg>
      </View>
    </ViewShot>
  );
}

function SignatureScreen({ route, navigation }){
  const { token } = useAuth();
  const { estimateId } = route.params;
  const [sigPng, setSigPng] = useState(null);
  const save = async () => {
    if(!sigPng){ Alert.alert('Please sign first'); return; }
    const resp = await api(`/estimates/${estimateId}/approve`, 'POST', { signaturePngUrl: sigPng }, token);
    Alert.alert('Approved', `Payment link:\n${(resp && resp.paymentLink) ? resp.paymentLink : 'Unavailable'}`); navigation.goBack();
  };
  return (
    <SafeAreaView style={{ padding:16 }}>
      <Text style={{ fontSize:18, marginBottom:8 }}>Sign Estimate #{estimateId}</Text>
      <SignaturePad onCapturePng={setSigPng} />
      <View style={{ height:12 }} />
      <Button title="Save Signature" onPress={save} />
    </SafeAreaView>
  );
}

// ---------- Admin: Users ----------
function UsersAdminScreen(){
  const { token } = useAuth();
  const [users, setUsers] = useState([]);
  const [email, setEmail] = useState('');
  const [fullName, setFullName] = useState('');
  const [role, setRole] = useState('TECH');
  const load = async ()=> setUsers(await api('/users','GET',null,token));
  useEffect(()=>{ load(); },[]);
  const invite = async ()=>{
    await api('/users','POST', { email, fullName, role: normalizeRole(role) }, token);
    setEmail(''); setFullName(''); setRole('TECH'); load();
  };
  const promote = async (id, newRole)=>{
    await api(`/users/${id}`,'PATCH',{ role: normalizeRole(newRole) }, token); load();
  };
  const remove = async (id) => {
    Alert.alert('Delete user', 'Are you sure?', [
      { text: 'Cancel', style: 'cancel' },
      { text: 'Delete', style: 'destructive', onPress: async ()=>{ await api(`/users/${id}`,'DELETE',null,token); load(); } }
    ]);
  };
  return (
    <SafeAreaView style={{ flex:1, padding:16 }}>
      <Text style={{ fontSize:18, fontWeight:'600' }}>Invite User</Text>
      <TextInput placeholder="Full name" value={fullName} onChangeText={setFullName} style={{ borderWidth:1, padding:8, marginVertical:6 }} />
      <TextInput placeholder="Email" value={email} onChangeText={setEmail} autoCapitalize='none' style={{ borderWidth:1, padding:8, marginVertical:6 }} />
      <TextInput placeholder="Role (ADMIN/ESTIMATOR/SUPERVISOR/TECH)" value={role} onChangeText={setRole} style={{ borderWidth:1, padding:8, marginVertical:6 }} />
      <Button title="Create User" onPress={invite} />
      <View style={{ height:16 }} />
      <FlatList data={users} keyExtractor={u=>String(u.id)} renderItem={({item}) => (
        <View style={{ padding:12, borderWidth:1, marginBottom:8 }}>
          <Text style={{ fontWeight:'600' }}>{item.fullName || item.email}</Text>
          <Text>{item.email}</Text>
          <Text>Role: {item.role}</Text>
          <View style={{ flexDirection:'row', gap:8, marginTop:8, flexWrap:'wrap' }}>
            <Button title="Admin" onPress={()=>promote(item.id,'ADMIN')} />
            <Button title="Estimator" onPress={()=>promote(item.id,'ESTIMATOR')} />
            <Button title="Supervisor" onPress={()=>promote(item.id,'SUPERVISOR')} />
            <Button title="Tech" onPress={()=>promote(item.id,'TECH')} />
            <View style={{ width:8 }} />
            <Button title="Delete" color="#c00" onPress={()=>remove(item.id)} />
          </View>
        </View>
      )}/>
    </SafeAreaView>
  );
}

// ---------- Jobs Kanban + Change Orders ----------
function JobsKanbanScreen({ navigation }){
  const { token } = useAuth();
  const [jobs, setJobs] = useState([]);
  const [loading, setLoading] = useState(false);
  const [newJobName, setNewJobName] = useState('');
  const [newJobDate, setNewJobDate] = useState('');
  const [newJobNotes, setNewJobNotes] = useState('');
  const [showJobDetails, setShowJobDetails] = useState(false);
  const [newCustomerName, setNewCustomerName] = useState('');
  const [newCustomerPhone, setNewCustomerPhone] = useState('');
  const [newCustomerEmail, setNewCustomerEmail] = useState('');
  const [newAddressLine1, setNewAddressLine1] = useState('');
  const [newAddressLine2, setNewAddressLine2] = useState('');
  const [newCity, setNewCity] = useState('');
  const [newStateCode, setNewStateCode] = useState('');
  const [newZip, setNewZip] = useState('');

  const load = useCallback(async () => {
    setLoading(true);
    try {
      const data = await api('/jobs','GET',null,token);
      setJobs(data);
      db.transaction(tx => {
        tx.executeSql('DELETE FROM jobs_cache;');
        data.forEach(j => tx.executeSql('INSERT OR REPLACE INTO jobs_cache (id, name, status, startDate, endDate, notes) VALUES (?,?,?,?,?,?)', [j.id, j.name||'', j.status||'SCHEDULED', j.startDate||'', j.endDate||'', j.notes||'']));
      });
    } catch(e) {
      db.transaction(tx => tx.executeSql('SELECT id, name, status, startDate, endDate, notes FROM jobs_cache', [], (_, { rows }) => setJobs(rows._array || [])));
    } finally {
      setLoading(false);
    }
  }, [token]);

  useFocusEffect(useCallback(() => {
    load();
  }, [load]));

  const resetForm = () => {
    setNewJobName('');
    setNewJobDate('');
    setNewJobNotes('');
    setNewCustomerName('');
    setNewCustomerPhone('');
    setNewCustomerEmail('');
    setNewAddressLine1('');
    setNewAddressLine2('');
    setNewCity('');
    setNewStateCode('');
    setNewZip('');
  };

  const createJob = async () => {
    if(!newJobName.trim()){
      Alert.alert('Missing info', 'Please add a job name.');
      return;
    }
    const hasCustomer = [newCustomerName, newCustomerPhone, newCustomerEmail].some(v => v && v.trim());
    const hasAddress = [newAddressLine1, newCity, newStateCode, newZip].some(v => v && v.trim());
    const payload = {
      name: newJobName.trim(),
      status: 'SCHEDULED',
      startDate: newJobDate || null,
      notes: newJobNotes.trim() || null,
    };
    if (hasCustomer) {
      payload.customer = {
        name: newCustomerName.trim(),
        phone: newCustomerPhone.trim(),
        email: newCustomerEmail.trim(),
        billingAddress: newAddressLine1 ? `${newAddressLine1}${newAddressLine2 ? `\n${newAddressLine2}` : ''}${newCity ? `\n${newCity}, ${newStateCode} ${newZip}` : ''}` : null,
      };
    }
    if (hasAddress) {
      payload.jobsite = {
        addressLine1: newAddressLine1.trim(),
        addressLine2: newAddressLine2.trim(),
        city: newCity.trim(),
        state: newStateCode.trim(),
        zip: newZip.trim(),
      };
    }
    try {
      const created = await api('/jobs','POST', payload, token);
      setJobs(prev => [created, ...prev]);
      db.transaction(tx => tx.executeSql('INSERT OR REPLACE INTO jobs_cache (id, name, status, startDate, endDate, notes) VALUES (?,?,?,?,?,?)', [created.id, created.name||'', created.status||'SCHEDULED', created.startDate||'', created.endDate||'', created.notes||'']));
      resetForm();
      Alert.alert('Job created', `Job #${created.id} ready to schedule.`);
    } catch(e) {
      Alert.alert('Error', e.message || 'Unable to create job');
    }
  };

  const setStatus = async (id, status) => {
    try {
      const updated = await api(`/jobs/${id}`,'PATCH',{ status }, token);
      setJobs(prev => prev.map(job => job.id === id ? updated : job));
    } catch(e) {
      Alert.alert('Error', e.message || 'Unable to update job status');
    }
  };

  const removeJob = async (id) => {
    Alert.alert('Delete Job', `Delete job #${id}?`, [
      { text:'Cancel', style:'cancel' },
      { text:'Delete', style:'destructive', onPress: async ()=>{
        try {
          await api(`/jobs/${id}`,'DELETE',null,token);
          setJobs(prev => prev.filter(job => job.id !== id));
          db.transaction(tx => tx.executeSql('DELETE FROM jobs_cache WHERE id=?', [id]));
        } catch(e) {
          Alert.alert('Error', e.message || 'Unable to delete job');
        }
      } }
    ]);
  };
  const columns = ['SCHEDULED','IN_PROGRESS','ON_HOLD','DONE'];
  return (
    <SafeAreaView style={{ flex:1 }}>
      <View style={{ padding:12, borderBottomWidth:1 }}>
        <Text style={{ fontWeight:'700', marginBottom:6 }}>New Job</Text>
        <View style={{ flexDirection:'row', gap:8, alignItems:'center' }}>
          <TextInput placeholder="Job name" value={newJobName} onChangeText={setNewJobName} style={{ flex:1, borderWidth:1, padding:8 }} />
          <TextInput placeholder="Start date (YYYY-MM-DD)" value={newJobDate} onChangeText={setNewJobDate} style={{ flex:1, borderWidth:1, padding:8 }} />
        </View>
        {showJobDetails && (
          <View style={{ marginTop:12 }}>
            <Text style={{ fontWeight:'600', marginBottom:4 }}>Notes</Text>
            <TextInput value={newJobNotes} onChangeText={setNewJobNotes} placeholder="Internal notes" multiline style={{ borderWidth:1, padding:8, minHeight:60, marginBottom:10 }} />
            <Text style={{ fontWeight:'600', marginBottom:4 }}>Customer</Text>
            <TextInput placeholder="Name" value={newCustomerName} onChangeText={setNewCustomerName} style={{ borderWidth:1, padding:8, marginBottom:6 }} />
            <TextInput placeholder="Phone" value={newCustomerPhone} onChangeText={setNewCustomerPhone} keyboardType="phone-pad" style={{ borderWidth:1, padding:8, marginBottom:6 }} />
            <TextInput placeholder="Email" value={newCustomerEmail} onChangeText={setNewCustomerEmail} keyboardType="email-address" style={{ borderWidth:1, padding:8, marginBottom:10 }} />
            <Text style={{ fontWeight:'600', marginBottom:4 }}>Job Address</Text>
            <TextInput placeholder="Address line 1" value={newAddressLine1} onChangeText={setNewAddressLine1} style={{ borderWidth:1, padding:8, marginBottom:6 }} />
            <TextInput placeholder="Address line 2" value={newAddressLine2} onChangeText={setNewAddressLine2} style={{ borderWidth:1, padding:8, marginBottom:6 }} />
            <View style={{ flexDirection:'row', gap:8 }}>
              <TextInput placeholder="City" value={newCity} onChangeText={setNewCity} style={{ flex:1, borderWidth:1, padding:8 }} />
              <TextInput placeholder="State" value={newStateCode} onChangeText={setNewStateCode} style={{ width:80, borderWidth:1, padding:8 }} />
              <TextInput placeholder="ZIP" value={newZip} onChangeText={setNewZip} style={{ width:100, borderWidth:1, padding:8 }} />
            </View>
          </View>
        )}
        <View style={{ flexDirection:'row', justifyContent:'space-between', alignItems:'center', marginTop:12 }}>
          <TouchableOpacity onPress={()=>setShowJobDetails(v => !v)}>
            <Text style={{ color:'#007AFF' }}>{showJobDetails ? 'Hide details' : 'Add customer & address'}</Text>
          </TouchableOpacity>
          <Button title="Create" onPress={createJob} />
        </View>
        {loading && <Text style={{ marginTop:8 }}>Refreshing jobsÃ¢â‚¬Â¦</Text>}
      </View>
      <ScrollView horizontal contentContainerStyle={{ padding:12 }}>
        {columns.map(col => (
          <View key={col} style={{ width:320, marginRight:16 }}>
            <Text style={{ fontWeight:'700', marginBottom:8 }}>{col}</Text>
        {jobs.filter(j=>j.status===col).length === 0 ? (
          <Text style={{ color:'#94a3b8', fontStyle:'italic' }}>No jobs here yet.</Text>
        ) : (
          jobs.filter(j=>j.status===col).map(job => {
            const customer = job.Customer || {};
            const jobsite = job.Jobsite || {};
                const addressParts = [
                  jobsite.addressLine1,
                  jobsite.addressLine2,
                  [jobsite.city, jobsite.state].filter(Boolean).join(', '),
                  jobsite.zip
                ].filter(Boolean);
                const address = addressParts.join(' Ã¢â‚¬Â¢ ');
                const quickStatuses = [
                  { label:'Start', value:'IN_PROGRESS', color:'#2563eb' },
                  { label:'Hold', value:'ON_HOLD', color:'#f97316' },
                  { label:'Done', value:'DONE', color:'#16a34a' },
                ];
                return (
                  <View key={job.id} style={{ backgroundColor:'#fff', borderRadius:12, padding:14, marginBottom:12, borderWidth:1, borderColor:'#e4e4e7', shadowColor:'#000', shadowOpacity:0.05, shadowRadius:6, elevation:1 }}>
                    <TouchableOpacity onPress={()=>navigation.navigate('JobDetail', { jobId: job.id })} activeOpacity={0.8}>
                      <Text style={{ fontWeight:'700', fontSize:16 }}>{job.name || `Job #${job.id}`}</Text>
                      <Text style={{ color:'#475569', marginTop:2 }}>Status: {job.status}</Text>
                      <Text style={{ color:'#475569', marginTop:2 }}>Start: {job.startDate || 'Ã¢â‚¬â€'} {job.endDate ? ` Ã¢â€ â€™ ${job.endDate}` : ''}</Text>
                      {customer.name ? <Text style={{ marginTop:6, fontWeight:'600' }}>{customer.name}</Text> : null}
                      {customer.phone ? <Text style={{ color:'#475569' }}>{customer.phone}</Text> : null}
                      {customer.email ? <Text style={{ color:'#475569' }}>{customer.email}</Text> : null}
                      {address ? <Text style={{ color:'#475569', marginTop:6 }}>{address}</Text> : null}
                      {job.notes ? <Text style={{ marginTop:6, color:'#334155' }}>{job.notes}</Text> : null}
                    </TouchableOpacity>
                    <View style={{ flexDirection:'row', flexWrap:'wrap', gap:8, marginTop:12 }}>
                      {quickStatuses.map(btn => (
                        <TouchableOpacity key={btn.value} onPress={()=>setStatus(job.id, btn.value)} style={{ backgroundColor:btn.color, paddingHorizontal:12, paddingVertical:6, borderRadius:16 }}>
                          <Text style={{ color:'#fff', fontWeight:'600' }}>{btn.label}</Text>
                        </TouchableOpacity>
                      ))}
                      <TouchableOpacity onPress={()=>removeJob(job.id)} style={{ paddingHorizontal:12, paddingVertical:6, borderRadius:16, borderWidth:1, borderColor:'#d11a2a' }}>
                        <Text style={{ color:'#d11a2a', fontWeight:'600' }}>Delete</Text>
                      </TouchableOpacity>
                    </View>
                  </View>
                );
              })
            )}
          </View>
        ))}
      </ScrollView>
    </SafeAreaView>
  );
}

function JobDetailScreen({ route, navigation }){
  const { jobId } = route.params;
  const { token, user } = useAuth();
  const [jobName, setJobName] = useState('');
  const [jobStartDate, setJobStartDate] = useState('');
  const [jobEndDate, setJobEndDate] = useState('');
  const [jobNotes, setJobNotes] = useState('');
  const [jobCustomerName, setJobCustomerName] = useState('');
  const [jobCustomerPhone, setJobCustomerPhone] = useState('');
  const [jobCustomerEmail, setJobCustomerEmail] = useState('');
  const [jobAddressLine1, setJobAddressLine1] = useState('');
  const [jobAddressLine2, setJobAddressLine2] = useState('');
  const [jobCity, setJobCity] = useState('');
  const [jobStateCode, setJobStateCode] = useState('');
  const [jobZip, setJobZip] = useState('');
  const [changeOrders, setCO] = useState([]);
  const [title, setTitle] = useState('');
  const [amountDelta, setAmountDelta] = useState('0');
  const loadCO = useCallback(async () => {
    setCO(await api(`/change-orders/job/${jobId}`,'GET',null,token));
  }, [jobId, token]);
  const loadJob = useCallback(async () => {
    try {
      const j = await api(`/jobs/${jobId}`,'GET',null,token);
      setJobName(j.name||'');
      setJobStartDate(j.startDate||'');
      setJobEndDate(j.endDate||'');
      setJobNotes(j.notes||'');
      const customer = j.Customer || {};
      const jobsite = j.Jobsite || {};
      setJobCustomerName(customer.name || '');
      setJobCustomerPhone(customer.phone || '');
      setJobCustomerEmail(customer.email || '');
      setJobAddressLine1(jobsite.addressLine1 || '');
      setJobAddressLine2(jobsite.addressLine2 || '');
      setJobCity(jobsite.city || '');
      setJobStateCode(jobsite.state || '');
      setJobZip(jobsite.zip || '');
    } catch(e) { }
  }, [jobId, token]);
  useEffect(()=>{ loadJob(); loadCO(); },[loadJob, loadCO]);
  const addCO = async ()=>{
    if(!title.trim()){
      Alert.alert('Missing info', 'Add a title for the change order.');
      return;
    }
    await api(`/change-orders/job/${jobId}`,'POST',{ title: title.trim(), amountDelta: parseFloat(amountDelta) || 0 }, token);
    setTitle(''); setAmountDelta('0'); loadCO();
  };
  const saveJobDetails = async () => {
    try {
      await api(`/jobs/${jobId}`,'PATCH',{
        name: jobName,
        startDate: jobStartDate || null,
        endDate: jobEndDate || null,
        notes: jobNotes,
        customer: {
          name: jobCustomerName,
          phone: jobCustomerPhone,
          email: jobCustomerEmail,
          billingAddress: jobAddressLine1 ? `${jobAddressLine1}${jobAddressLine2 ? `\n${jobAddressLine2}` : ''}${jobCity ? `\n${jobCity}, ${jobStateCode} ${jobZip}` : ''}` : null,
        },
        jobsite: {
          addressLine1: jobAddressLine1,
          addressLine2: jobAddressLine2,
          city: jobCity,
          state: jobStateCode,
          zip: jobZip,
        },
      }, token);
      Alert.alert('Saved', 'Job details updated.');
      loadJob();
    } catch(e) {
      Alert.alert('Error', e.message || 'Unable to save job');
    }
  };
  const deleteJob = () => {
    Alert.alert('Delete job', `Delete job #${jobId}?`, [
      { text:'Cancel', style:'cancel' },
      { text:'Delete', style:'destructive', onPress: async ()=>{
        try{
          await api(`/jobs/${jobId}`,'DELETE',null,token);
          db.transaction(tx => tx.executeSql('DELETE FROM jobs_cache WHERE id=?', [jobId]));
          Alert.alert('Deleted', 'Job removed.');
          navigation.navigate('Tabs', { screen:'Jobs' });
        }catch(e){
          Alert.alert('Error', e.message || 'Unable to delete job');
        }
      }}
    ]);
  };
  return (
    <SafeAreaView style={{ flex:1 }}>
      <ScrollView contentContainerStyle={{ padding:16, paddingBottom:32 }}>
        <Text style={{ fontSize:18, fontWeight:'600', marginBottom:8 }}>Job #{jobId}</Text>
        <Text style={{ fontWeight:'600', marginBottom:6 }}>Job Details</Text>
        <TextInput placeholder="Job name" value={jobName} onChangeText={setJobName} style={{ borderWidth:1, padding:8, marginBottom:6 }} />
        <View style={{ flexDirection:'row', gap:8 }}>
          <TextInput placeholder="Start date (YYYY-MM-DD)" value={jobStartDate} onChangeText={setJobStartDate} style={{ flex:1, borderWidth:1, padding:8 }} />
          <TextInput placeholder="End date (YYYY-MM-DD)" value={jobEndDate} onChangeText={setJobEndDate} style={{ flex:1, borderWidth:1, padding:8 }} />
        </View>
        <TextInput placeholder="Notes / scope" value={jobNotes} onChangeText={setJobNotes} multiline style={{ borderWidth:1, padding:8, marginTop:6, minHeight:80 }} />

        <Text style={{ fontWeight:'600', marginTop:16 }}>Customer</Text>
        <TextInput placeholder="Name" value={jobCustomerName} onChangeText={setJobCustomerName} style={{ borderWidth:1, padding:8, marginBottom:6 }} />
        <TextInput placeholder="Phone" value={jobCustomerPhone} onChangeText={setJobCustomerPhone} keyboardType="phone-pad" style={{ borderWidth:1, padding:8, marginBottom:6 }} />
        <TextInput placeholder="Email" value={jobCustomerEmail} onChangeText={setJobCustomerEmail} keyboardType="email-address" style={{ borderWidth:1, padding:8, marginBottom:6 }} />

        <Text style={{ fontWeight:'600', marginTop:16 }}>Job Address</Text>
        <TextInput placeholder="Address line 1" value={jobAddressLine1} onChangeText={setJobAddressLine1} style={{ borderWidth:1, padding:8, marginBottom:6 }} />
        <TextInput placeholder="Address line 2" value={jobAddressLine2} onChangeText={setJobAddressLine2} style={{ borderWidth:1, padding:8, marginBottom:6 }} />
        <View style={{ flexDirection:'row', gap:8 }}>
          <TextInput placeholder="City" value={jobCity} onChangeText={setJobCity} style={{ flex:1, borderWidth:1, padding:8 }} />
          <TextInput placeholder="State" value={jobStateCode} onChangeText={setJobStateCode} style={{ width:80, borderWidth:1, padding:8 }} />
          <TextInput placeholder="ZIP" value={jobZip} onChangeText={setJobZip} style={{ width:100, borderWidth:1, padding:8 }} />
        </View>
        <View style={{ marginTop:12 }}>
          <Button title="Save details" onPress={saveJobDetails} />
        </View>
        <View style={{ marginTop:12 }}>
          <Button color="#d11a2a" title="Delete job" onPress={deleteJob} />
        </View>

        {(user?.role==='ADMIN'||user?.role==='SUPERVISOR'||user?.role==='ESTIMATOR') && (
          <View style={{ marginVertical:16 }}>
            <Text style={{ fontWeight:'600' }}>New Change Order</Text>
            <TextInput placeholder="Title" value={title} onChangeText={setTitle} style={{ borderWidth:1, padding:8, marginVertical:6 }} />
            <TextInput placeholder="Amount $" value={amountDelta} onChangeText={setAmountDelta} keyboardType="decimal-pad" style={{ borderWidth:1, padding:8, marginVertical:6 }} />
            <Button title="Add Change Order" onPress={addCO} />
          </View>
        )}
        <Text style={{ fontWeight:'600', marginTop:12 }}>Change Orders</Text>
        {changeOrders.length === 0 ? (
          <Text style={{ marginVertical:6 }}>No change orders yet.</Text>
        ) : changeOrders.map(item => (
          <View key={item.id} style={{ borderWidth:1, padding:10, borderRadius:8, marginVertical:6 }}>
            <Text style={{ fontWeight:'600' }}>{item.title}</Text>
            <Text>Status: {item.status} | ${item.amountDelta}</Text>
            {(user?.role==='ADMIN'||user?.role==='SUPERVISOR'||user?.role==='ESTIMATOR') && (
              <View style={{ flexDirection:'row', gap:8, marginTop:8 }}>
                <Button title="Approve" onPress={async ()=>{ await api(`/change-orders/${item.id}`,'PATCH',{ status:'APPROVED' }, token); loadCO(); }} />
                <Button title="Reject" onPress={async ()=>{ await api(`/change-orders/${item.id}`,'PATCH',{ status:'REJECTED' }, token); loadCO(); }} />
              </View>
            )}
          </View>
        ))}

        <Text style={{ fontWeight:'600', marginTop:16 }}>Tasks</Text>
        <JobTasks jobId={jobId} />

        <Text style={{ fontWeight:'600', marginTop:16 }}>Schedule</Text>
        <JobSchedule jobId={jobId} />
      </ScrollView>
    </SafeAreaView>
  );
}

function JobTasks({ jobId }){
  const { token } = useAuth();
  const [tasks, setTasks] = useState([]);
  const [title, setTitle] = useState('');
  const [dueDate, setDueDate] = useState('');
  const load = async()=> {
    try {
      const data = await api(`/tasks?jobId=${jobId}`,'GET',null,token);
      setTasks(data);
      db.transaction(tx => {
        tx.executeSql('DELETE FROM tasks_cache WHERE jobId=?', [jobId]);
        data.forEach(t => tx.executeSql('INSERT OR REPLACE INTO tasks_cache (id, jobId, title, status, dueDate) VALUES (?,?,?,?,?)', [t.id, jobId, t.title||'', t.status||'TODO', t.dueDate||'']));
      });
    } catch(e) {
      db.transaction(tx => tx.executeSql('SELECT id, jobId, title, status, dueDate FROM tasks_cache WHERE jobId=?', [jobId], (_, { rows }) => setTasks(rows._array || [])));
    }
  };
  useEffect(()=>{ load(); },[]);
  const add = async()=>{
    if(!title.trim()) return Alert.alert('Title required');
    await api('/tasks','POST',{ title, jobId, dueDate }, token);
    setTitle(''); setDueDate(''); load();
  };
  const done = async(id)=>{ await api(`/tasks/${id}`,'PATCH',{ status:'DONE' }, token); load(); };
  const remove = async(id)=>{ await api(`/tasks/${id}`,'DELETE',null,token); load(); };
  return (
    <View>
      <View style={{ flexDirection:'row', gap:8, marginVertical:8 }}>
        <TextInput placeholder="Task title" value={title} onChangeText={setTitle} style={{ flex:1, borderWidth:1, padding:8 }} />
        <TextInput placeholder="Due YYYY-MM-DD" value={dueDate} onChangeText={setDueDate} style={{ width:140, borderWidth:1, padding:8 }} />
        <Button title="Add" onPress={add} />
      </View>
      {tasks.map(t => (
        <View key={t.id} style={{ borderWidth:1, padding:10, borderRadius:8, marginVertical:6 }}>
          <Text style={{ fontWeight:'600' }}>{t.title}</Text>
          <Text>Due: {t.dueDate || '-'} | Status: {t.status}</Text>
          <View style={{ flexDirection:'row', gap:8, marginTop:6 }}>
            <Button title="Mark Done" onPress={()=>done(t.id)} />
            <Button title="Delete" color="#c00" onPress={()=>remove(t.id)} />
          </View>
        </View>
      ))}
    </View>
  );
}

function JobSchedule({ jobId }){
  const { token } = useAuth();
  const [events, setEvents] = useState([]);
  const [title, setTitle] = useState('');
  const [startAt, setStartAt] = useState('');
  const [endAt, setEndAt] = useState('');
  const load = async()=> {
    try {
      const data = await api(`/calendar?jobId=${jobId}`,'GET',null,token);
      setEvents(data);
      db.transaction(tx => {
        tx.executeSql('DELETE FROM calendar_cache WHERE jobId=?', [jobId]);
        data.forEach(ev => tx.executeSql('INSERT OR REPLACE INTO calendar_cache (id, jobId, title, startAt, endAt) VALUES (?,?,?,?,?)', [ev.id, jobId, ev.title||'', ev.startAt||'', ev.endAt||'']));
      });
    } catch(e) {
      db.transaction(tx => tx.executeSql('SELECT id, jobId, title, startAt, endAt FROM calendar_cache WHERE jobId=?', [jobId], (_, { rows }) => setEvents(rows._array || [])));
    }
  };
  useEffect(()=>{ load(); },[]);
  const add = async()=>{
    if(!title.trim() || !startAt) return Alert.alert('Title and start time required');
    await api('/calendar','POST',{ title, jobId, startAt, endAt }, token);
    setTitle(''); setStartAt(''); setEndAt(''); load();
  };
  const remove = async(id)=>{ await api(`/calendar/${id}`,'DELETE',null,token); load(); };
  return (
    <View>
      <View style={{ marginVertical:8 }}>
        <TextInput placeholder="Event title" value={title} onChangeText={setTitle} style={{ borderWidth:1, padding:8, marginBottom:6 }} />
        <TextInput placeholder="Start (YYYY-MM-DDTHH:mm:ssZ)" value={startAt} onChangeText={setStartAt} style={{ borderWidth:1, padding:8, marginBottom:6 }} />
        <TextInput placeholder="End (YYYY-MM-DDTHH:mm:ssZ)" value={endAt} onChangeText={setEndAt} style={{ borderWidth:1, padding:8, marginBottom:6 }} />
        <Button title="Add Event" onPress={add} />
      </View>
      {events.map(ev => (
        <View key={ev.id} style={{ borderWidth:1, padding:10, borderRadius:8, marginVertical:6 }}>
          <Text style={{ fontWeight:'600' }}>{ev.title}</Text>
          <Text>{ev.startAt} -> {ev.endAt || '-'}</Text>
          <Button title="Delete" color="#c00" onPress={()=>remove(ev.id)} />
        </View>
      ))}
    </View>
  );
}

