async function processQueue(token){
  return new Promise(resolve => {
    db.transaction(tx => {
      tx.executeSql('SELECT * FROM queue', [], async (_, { rows }) => {
        const items = rows._array || [];
        for(const q of items){
          try{
            const payload = JSON.parse(q.payload);
            if(q.type === 'createLead'){ await api('/leads', 'POST', payload, token); }
            if(q.type === 'uploadPhoto'){ await api('/upload/image', 'POST', { dataUrl: payload.dataUrl }, token); }
            db.transaction(txx => txx.executeSql('DELETE FROM queue WHERE id=?', [q.id]));
          }catch(e){ /* keep for later */ }
        }
        resolve();
      });
    });
  });
}

function LeadsScreen({ navigation }){
  const { token } = useAuth();
  const [leads, setLeads] = useState([]);
  const [refreshing, setRefreshing] = useState(false);
  const fetchLeads = async () => {
    try {
      const data = await api('/leads', 'GET', null, token);
      setLeads(data);
      db.transaction(tx => {
        tx.executeSql('DELETE FROM leads_cache;');
        data.forEach(l => tx.executeSql('INSERT OR REPLACE INTO leads_cache (id, description, status) VALUES (?,?,?)', [l.id, l.description||'', l.status||'NEW']));
      });
    } catch(e){
      db.transaction(tx => tx.executeSql('SELECT id, description, status FROM leads_cache', [], (_, { rows }) => setLeads(rows._array || [])));
    }
  };
  useFocusEffect(useCallback(() => {
    fetchLeads();
  }, [token]));
  const onRefresh = async () => {
    setRefreshing(true);
    await fetchLeads();
    setRefreshing(false);
  };
  const deleteLead = (lead) => {
    Alert.alert('Delete lead', `Delete lead #${lead.id}?`, [
      { text:'Cancel', style:'cancel' },
      { text:'Delete', style:'destructive', onPress: async ()=>{
        try{
          await api(`/leads/${lead.id}`,'DELETE',null,token);
          setLeads(prev => prev.filter(l => l.id !== lead.id));
          db.transaction(tx => tx.executeSql('DELETE FROM leads_cache WHERE id=?', [lead.id]));
        }catch(e){
          Alert.alert('Error', e.message || 'Unable to delete lead');
        }
      }}
    ]);
  };
  return (
    <SafeAreaView style={{ flex:1 }}>
      <FlatList
        data={leads}
        keyExtractor={item => String(item.id)}
        contentContainerStyle={{ padding:12 }}
        refreshControl={<RefreshControl refreshing={refreshing} onRefresh={onRefresh} />}
        renderItem={({ item }) => {
          const customer = item.Customer || {};
          const jobsite = item.Jobsite || {};
          const addressParts = [
            jobsite.addressLine1,
            jobsite.addressLine2,
            [jobsite.city, jobsite.state].filter(Boolean).join(', '),
            jobsite.zip
          ].filter(Boolean);
          const address = addressParts.join(' Ã¢â‚¬Â¢ ');
          return (
            <View style={{ backgroundColor:'#fff', borderRadius:12, padding:14, marginBottom:12, borderWidth:1, borderColor:'#e4e4e7', shadowColor:'#000', shadowOpacity:0.04, shadowRadius:6, elevation:1 }}>
              <View style={{ flexDirection:'row', justifyContent:'space-between', alignItems:'center', marginBottom:8 }}>
                <View>
                  <Text style={{ fontWeight:'700', fontSize:16 }}>Lead #{item.id}</Text>
                  <Text style={{ color:'#007AFF', fontWeight:'600' }}>{item.status}</Text>
                </View>
                <TouchableOpacity onPress={()=>deleteLead(item)} style={{ paddingVertical:4, paddingHorizontal:8 }}>
                  <Text style={{ color:'#d11a2a', fontWeight:'600' }}>Delete</Text>
                </TouchableOpacity>
              </View>
              {customer.name ? <Text style={{ fontWeight:'600' }}>{customer.name}</Text> : null}
              {customer.phone ? <Text style={{ color:'#475569' }}>{customer.phone}</Text> : null}
              {customer.email ? <Text style={{ color:'#475569' }}>{customer.email}</Text> : null}
              {address ? <Text style={{ color:'#475569', marginTop:6 }}>{address}</Text> : null}
              <Text numberOfLines={3} style={{ marginTop:8, color:'#1f2937' }}>{item.description || 'No description yet.'}</Text>
              <TouchableOpacity onPress={()=>navigation.navigate('EstimateEditor', { leadId: item.id })} style={{ backgroundColor:'#007AFF', borderRadius:8, paddingVertical:10, paddingHorizontal:16, alignSelf:'flex-start', marginTop:14 }}>
                <Text style={{ color:'#fff', fontWeight:'600' }}>Open lead</Text>
              </TouchableOpacity>
            </View>
          );
        }}
        ListHeaderComponent={<Button title="New Lead" onPress={()=>navigation.navigate('NewLead')} />}
      />
    </SafeAreaView>
  );
}

function NewLeadScreen({ navigation }){
  const { token } = useAuth();
  const [description, setDescription] = useState('');
  const [status, setStatus] = useState('NEW');
  const [customerName, setCustomerName] = useState('');
  const [customerPhone, setCustomerPhone] = useState('');
  const [customerEmail, setCustomerEmail] = useState('');
  const [addressLine1, setAddressLine1] = useState('');
  const [addressLine2, setAddressLine2] = useState('');
  const [city, setCity] = useState('');
  const [stateCode, setStateCode] = useState('');
  const [zip, setZip] = useState('');
  const statuses = ['NEW','CONTACTED','ESTIMATING','CONVERTED','CLOSED_LOST'];

  const buildPayload = () => {
    const customer = [customerName, customerPhone, customerEmail].some(v => v && v.trim())
      ? {
          name: customerName.trim(),
          phone: customerPhone.trim(),
          email: customerEmail.trim(),
        }
      : null;
    const jobsite = [addressLine1, addressLine2, city, stateCode, zip].some(v => v && v.trim())
      ? {
          addressLine1: addressLine1.trim(),
          addressLine2: addressLine2.trim(),
          city: city.trim(),
          state: stateCode.trim(),
          zip: zip.trim(),
        }
      : null;
    return {
      description,
      status,
      customer,
      jobsite,
    };
  };

  const createLeadOnline = async () => {
    const payload = buildPayload();
    const created = await api('/leads', 'POST', payload, token);
    navigation.replace('EstimateEditor', { leadId: created.id });
    Alert.alert('Lead created', `Lead #${created.id} ready to estimate.`);
  };

  const queueLead = () => {
    const payload = buildPayload();
    db.transaction(tx => tx.executeSql('INSERT INTO queue (type, payload) VALUES (?,?)', ['createLead', JSON.stringify(payload)]));
    Alert.alert('Saved offline', 'We will submit this lead when you reconnect.');
    navigation.goBack();
  };

  const submit = async () => {
    if(!description.trim()){
      Alert.alert('Missing scope', 'Please describe the job or scope of work.');
      return;
    }
    try {
      await createLeadOnline();
    } catch(e){
      queueLead();
    }
  };

  return (
    <SafeAreaView style={{ flex:1 }}>
      <ScrollView contentContainerStyle={{ padding:16, paddingBottom:32 }}>
        <Text style={{ fontSize:18, marginBottom:8 }}>New Lead</Text>
        <Text style={{ fontWeight:'600', marginBottom:6 }}>Scope / Notes</Text>
        <TextInput
          placeholder="Describe the job..."
          value={description}
          onChangeText={setDescription}
          multiline
          style={{ borderWidth:1, minHeight:120, padding:10, marginBottom:12 }}
        />
        <Text style={{ fontWeight:'600', marginBottom:6 }}>Lead status</Text>
        <View style={{ flexDirection:'row', flexWrap:'wrap', gap:8, marginBottom:12 }}>
          {statuses.map(s => (
            <TouchableOpacity
              key={s}
              onPress={()=>setStatus(s)}
              style={{
                paddingVertical:6,
                paddingHorizontal:12,
                borderRadius:16,
                borderWidth:1,
                borderColor: status===s ? '#007AFF' : '#ccc',
                backgroundColor: status===s ? '#E6F0FF' : '#fff'
              }}
            >
              <Text style={{ color: status===s ? '#007AFF' : '#333' }}>{s}</Text>
            </TouchableOpacity>
          ))}
        </View>

        <Text style={{ fontWeight:'600', marginBottom:6 }}>Customer</Text>
        <TextInput placeholder="Name" value={customerName} onChangeText={setCustomerName} style={{ borderWidth:1, padding:8, marginBottom:6 }} />
        <TextInput placeholder="Phone" value={customerPhone} onChangeText={setCustomerPhone} keyboardType="phone-pad" style={{ borderWidth:1, padding:8, marginBottom:6 }} />
        <TextInput placeholder="Email" value={customerEmail} onChangeText={setCustomerEmail} keyboardType="email-address" style={{ borderWidth:1, padding:8, marginBottom:12 }} />

        <Text style={{ fontWeight:'600', marginBottom:6 }}>Job address</Text>
        <TextInput placeholder="Address line 1" value={addressLine1} onChangeText={setAddressLine1} style={{ borderWidth:1, padding:8, marginBottom:6 }} />
        <TextInput placeholder="Address line 2" value={addressLine2} onChangeText={setAddressLine2} style={{ borderWidth:1, padding:8, marginBottom:6 }} />
        <View style={{ flexDirection:'row', gap:8 }}>
          <TextInput placeholder="City" value={city} onChangeText={setCity} style={{ flex:1, borderWidth:1, padding:8 }} />
          <TextInput placeholder="State" value={stateCode} onChangeText={setStateCode} style={{ width:80, borderWidth:1, padding:8 }} />
          <TextInput placeholder="ZIP" value={zip} onChangeText={setZip} style={{ width:100, borderWidth:1, padding:8 }} />
        </View>

        <View style={{ marginTop:16 }}>
          <Button title="Create Lead" onPress={submit} />
        </View>
      </ScrollView>
    </SafeAreaView>
  );
}

function EstimateEditorScreen({ route, navigation }){
  const { token, user } = useAuth();
  const { leadId } = route.params || {};
  const [estimateId, setEstimateId] = useState(null);
  const [items, setItems] = useState([]);
  const [desc, setDesc] = useState('');
  const [qty, setQty] = useState('1');
  const [price, setPrice] = useState('0');
  const [notes, setNotes] = useState('');
  const [photoUrl, setPhotoUrl] = useState(null);
  const [subtotal, setSubtotal] = useState(0);
  const [taxRate, setTaxRate] = useState(7.0);
  const [tax, setTax] = useState(0);
  const [total, setTotal] = useState(0);
  const [leadDetails, setLeadDetails] = useState(null);
  const [leadStatus, setLeadStatus] = useState('NEW');
  const [leadScope, setLeadScope] = useState('');
  const [leadCustomerName, setLeadCustomerName] = useState('');
  const [leadCustomerPhone, setLeadCustomerPhone] = useState('');
  const [leadCustomerEmail, setLeadCustomerEmail] = useState('');
  const [leadAddressLine1, setLeadAddressLine1] = useState('');
  const [leadAddressLine2, setLeadAddressLine2] = useState('');
  const [leadCity, setLeadCity] = useState('');
  const [leadStateCode, setLeadStateCode] = useState('');
  const [leadZip, setLeadZip] = useState('');
  const leadStatuses = ['NEW','CONTACTED','ESTIMATING','CONVERTED','CLOSED_LOST'];

  const loadLead = useCallback(async () => {
    if (!leadId) return;
    try {
      const data = await api(`/leads/${leadId}`,'GET',null,token);
      setLeadDetails(data);
      setLeadStatus(data.status || 'NEW');
      setLeadScope(data.description || '');
      const customer = data.Customer || {};
      const jobsite = data.Jobsite || {};
      setLeadCustomerName(customer.name || '');
      setLeadCustomerPhone(customer.phone || '');
      setLeadCustomerEmail(customer.email || '');
      setLeadAddressLine1(jobsite.addressLine1 || '');
      setLeadAddressLine2(jobsite.addressLine2 || '');
      setLeadCity(jobsite.city || '');
      setLeadStateCode(jobsite.state || '');
      setLeadZip(jobsite.zip || '');
    } catch(e) { }
  }, [leadId, token]);

  useEffect(() => {
    loadLead();
  }, [loadLead]);

  const createEstimate = async () => {
    const est = await api('/estimates', 'POST', {
      leadId,
      customerId: leadDetails?.customerId ?? null,
      jobsiteId: leadDetails?.jobsiteId ?? null,
      subtotal: 0,
      taxRate: parseFloat(taxRate) || 0,
      total: 0
    }, token);
    setEstimateId(est.id);
    return est;
  };

  const addItem = async () => {
    if(!estimateId){ Alert.alert('Create estimate first'); return; }
            <Text>Qty: {item.qty} x ${item.unitPrice} = {(parseFloat(item.qty||0)*parseFloat(item.unitPrice||0)).toFixed(2)}</Text>
    setItems(prev => [data, ...prev]); setDesc(''); setQty('1'); setPrice('0');
  };

  const suggest = async () => {
    const r = await api('/ai/estimate-suggest', 'POST', { notes }, token);
    let targetId = estimateId;
    if(!targetId){
      const est = await createEstimate();
      targetId = est.id;
    }
    for(const it of r.items){
      await api(`/estimates/${targetId}/items`, 'POST', it, token);
    }
    if(!estimateId) setEstimateId(targetId);
    Alert.alert('Added', 'Suggested line-items added');
  };

  const pickPhoto = async () => {
    const res = await ImagePicker.launchImageLibraryAsync({ base64: true, quality: 0.6 });
    if(!res.canceled && res.assets && res.assets[0].base64){
      const dataUrl = `data:image/jpeg;base64,${res.assets[0].base64}`;
      try {
        const up = await api('/upload/image', 'POST', { dataUrl }, token);
        setPhotoUrl(up.url);
      } catch(e){
        db.transaction(tx => tx.executeSql('INSERT INTO queue (type, payload) VALUES (?,?)', ['uploadPhoto', JSON.stringify({ dataUrl })]));
        Alert.alert('Queued upload', 'Will send when online');
      }
    }
  };

  const saveLeadDetails = async () => {
    if (!leadId) return;
    try {
      const updated = await api(`/leads/${leadId}`,'PATCH',{
        description: leadScope,
        status: leadStatus,
        customer: [leadCustomerName, leadCustomerPhone, leadCustomerEmail].some(v => v && v.trim()) ? {
          name: leadCustomerName,
          phone: leadCustomerPhone,
          email: leadCustomerEmail,
        } : null,
        jobsite: [leadAddressLine1, leadAddressLine2, leadCity, leadStateCode, leadZip].some(v => v && v.trim()) ? {
          addressLine1: leadAddressLine1,
          addressLine2: leadAddressLine2,
          city: leadCity,
          state: leadStateCode,
          zip: leadZip,
        } : null,
      }, token);
      setLeadDetails(updated);
      Alert.alert('Saved', 'Lead details updated.');
    } catch(e) {
      Alert.alert('Error', e.message || 'Unable to update lead');
    }
  };

  const approveAndSign = () => { if(!estimateId){ Alert.alert('Create estimate'); return; } navigation.navigate('Signature', { estimateId }); };

  const canEstimate = user?.role === 'ADMIN' || user?.role === 'ESTIMATOR';

  useEffect(()=>{
    const s = items.reduce((acc, it) => acc + (parseFloat(it.qty||0) * parseFloat(it.unitPrice||0)), 0);
    const t = s * (parseFloat(taxRate||0)/100);
    setSubtotal(s);
    setTax(t);
    setTotal(s + t);
  }, [items, taxRate]);

  return (
    <SafeAreaView style={{ flex:1 }}>
      <ScrollView contentContainerStyle={{ padding:16, paddingBottom:32 }}>
        <Text style={{ fontSize:18, marginBottom:8 }}>Lead #{leadId}</Text>
        <Text style={{ fontWeight:'600', marginBottom:6 }}>Lead status</Text>
        <View style={{ flexDirection:'row', flexWrap:'wrap', gap:8, marginBottom:12 }}>
          {leadStatuses.map(s => (
            <TouchableOpacity
              key={s}
              onPress={()=>setLeadStatus(s)}
              style={{
                paddingVertical:6,
                paddingHorizontal:12,
                borderRadius:16,
                borderWidth:1,
                borderColor: leadStatus===s ? '#007AFF' : '#ccc',
                backgroundColor: leadStatus===s ? '#E6F0FF' : '#fff'
              }}
            >
              <Text style={{ color: leadStatus===s ? '#007AFF' : '#333' }}>{s}</Text>
            </TouchableOpacity>
          ))}
        </View>
        <Text style={{ fontWeight:'600', marginBottom:6 }}>Scope / notes</Text>
        <TextInput
          placeholder="What does this lead need? Add notes, access info, budget..."
          value={leadScope}
          onChangeText={setLeadScope}
          multiline
          style={{ borderWidth:1, padding:10, minHeight:100, marginBottom:12 }}
        />
        <Text style={{ fontWeight:'600', marginBottom:6 }}>Customer</Text>
        <TextInput placeholder="Name" value={leadCustomerName} onChangeText={setLeadCustomerName} style={{ borderWidth:1, padding:8, marginBottom:6 }} />
        <TextInput placeholder="Phone" value={leadCustomerPhone} onChangeText={setLeadCustomerPhone} keyboardType="phone-pad" style={{ borderWidth:1, padding:8, marginBottom:6 }} />
        <TextInput placeholder="Email" value={leadCustomerEmail} onChangeText={setLeadCustomerEmail} keyboardType="email-address" style={{ borderWidth:1, padding:8, marginBottom:12 }} />
        <Text style={{ fontWeight:'600', marginBottom:6 }}>Job address</Text>
        <TextInput placeholder="Address line 1" value={leadAddressLine1} onChangeText={setLeadAddressLine1} style={{ borderWidth:1, padding:8, marginBottom:6 }} />
        <TextInput placeholder="Address line 2" value={leadAddressLine2} onChangeText={setLeadAddressLine2} style={{ borderWidth:1, padding:8, marginBottom:6 }} />
        <View style={{ flexDirection:'row', gap:8 }}>
          <TextInput placeholder="City" value={leadCity} onChangeText={setLeadCity} style={{ flex:1, borderWidth:1, padding:8 }} />
          <TextInput placeholder="State" value={leadStateCode} onChangeText={setLeadStateCode} style={{ width:80, borderWidth:1, padding:8 }} />
          <TextInput placeholder="ZIP" value={leadZip} onChangeText={setLeadZip} style={{ width:100, borderWidth:1, padding:8 }} />
        </View>
        <View style={{ marginTop:12 }}>
          <Button title="Save lead details" onPress={saveLeadDetails} />
        </View>

        <View style={{ height:24 }} />
        <Text style={{ fontSize:18, fontWeight:'600', marginBottom:8 }}>Estimate</Text>
        {canEstimate ? <Button title={estimateId ? `Estimate #${estimateId}` : "Create Estimate"} onPress={createEstimate} /> : <Text>View only (insufficient role)</Text>}
        <View style={{ height:12 }} />
        <TextInput placeholder="Item description" value={desc} onChangeText={setDesc} style={{ borderWidth:1, padding:8, marginBottom:6 }} />
        <TextInput placeholder="Qty" value={qty} onChangeText={setQty} keyboardType='decimal-pad' style={{ borderWidth:1, padding:8, marginBottom:6 }} />
        <TextInput placeholder="Unit Price" value={price} onChangeText={setPrice} keyboardType='decimal-pad' style={{ borderWidth:1, padding:8, marginBottom:6 }} />
        {canEstimate && <Button title="Add Item" onPress={addItem} />}
        <View style={{ height:16 }} />
        <Text style={{ fontWeight:'600' }}>AI helper</Text>
        <TextInput placeholder="Paste field notes..." value={notes} onChangeText={setNotes} multiline style={{ borderWidth:1, minHeight:80, padding:8, marginBottom:6 }} />
        {canEstimate && <Button title="Suggest Line Items" onPress={suggest} />}
        <View style={{ height:16 }} />
        <Text style={{ fontWeight:'600', marginBottom:6 }}>Totals</Text>
        <View style={{ flexDirection:'row', justifyContent:'space-between', marginBottom:6 }}>
          <Text>Subtotal</Text><Text>${subtotal.toFixed(2)}</Text>
        </View>
        <View style={{ flexDirection:'row', alignItems:'center', justifyContent:'space-between', marginBottom:6 }}>
          <Text>Tax Rate (%)</Text>
          <TextInput value={String(taxRate)} onChangeText={(v)=>setTaxRate(parseFloat(v)||0)} keyboardType='decimal-pad' style={{ borderWidth:1, padding:6, width:80, textAlign:'right' }} />
        </View>
        <View style={{ flexDirection:'row', justifyContent:'space-between', marginBottom:6 }}>
          <Text>Tax</Text><Text>${tax.toFixed(2)}</Text>
        </View>
        <View style={{ flexDirection:'row', justifyContent:'space-between', marginBottom:12 }}>
          <Text style={{ fontWeight:'700' }}>Total</Text><Text style={{ fontWeight:'700' }}>${total.toFixed(2)}</Text>
        </View>
        <View style={{ height:8 }} />
        <Button title="Attach Photo" onPress={pickPhoto} />
        {photoUrl ? <Image source={{ uri: `${API_URL}${photoUrl}` }} style={{ height:120, marginTop:8 }} /> : null}
        <View style={{ height:16 }} />
        <Button title="Create Job from Estimate" onPress={async ()=>{
          try {
            let targetId = estimateId;
            if(!targetId){
              const est = await createEstimate();
              targetId = est.id;
            }
            const hasCustomer = [leadCustomerName, leadCustomerPhone, leadCustomerEmail].some(v => v && v.trim());
            const hasAddress = [leadAddressLine1, leadAddressLine2, leadCity, leadStateCode, leadZip].some(v => v && v.trim());
            const payload = {
              name: leadCustomerName ? `${leadCustomerName} - ${(leadScope || 'Job').slice(0,40)}` : `Job for Estimate #${targetId}`,
              status: 'SCHEDULED',
              estimateId: targetId,
              notes: leadScope || null,
            };
            if (hasCustomer) {
              payload.customer = {
                name: leadCustomerName,
                phone: leadCustomerPhone,
                email: leadCustomerEmail,
              };
            }
            if (hasAddress) {
              payload.jobsite = {
                addressLine1: leadAddressLine1,
                addressLine2: leadAddressLine2,
                city: leadCity,
                state: leadStateCode,
                zip: leadZip,
              };
            }
            const job = await api('/jobs','POST',payload, token);
            Alert.alert('Job Created', `Job #${job.id}`, [
              { text:'Open job', onPress: ()=>navigation.navigate('JobDetail', { jobId: job.id }) },
              { text:'OK' }
            ]);
          } catch(e){ Alert.alert('Error', e.message); }
        }} />
        <View style={{ height:8 }} />
        <Button title="Approve & Sign" onPress={approveAndSign} />
        <View style={{ height:8 }} />
        <Button title="Send Estimate" onPress={async ()=>{
          if(!estimateId) return Alert.alert('Create estimate first');
          try { await api(`/estimates/${estimateId}/send`, 'POST', {}, token); Alert.alert('Sent', 'Estimate sent'); } catch(e){ Alert.alert('Error', e.message); }
        }} />
        <View style={{ height:8 }} />
        <Button title="Download PDF" onPress={async ()=>{
          if(!estimateId) return Alert.alert('Create estimate first');
          const fileUri = `${FileSystem.documentDirectory}estimate-${estimateId}.pdf`;
          try {
            await FileSystem.downloadAsync(`${API_URL}/pdf/estimate/${estimateId}`, fileUri, { headers: { Authorization: `Bearer ${token}` } });
            Alert.alert('Saved', `PDF saved to:\n${fileUri}`);
          } catch (e) { Alert.alert('Error', e.message); }
        }} />
        <View style={{ height:16 }} />
        {items.length === 0 ? (
          <Text style={{ color:'#555' }}>No line-items yet.</Text>
        ) : items.map((item, idx) => (
          <View key={item.id || idx} style={{ padding:10, borderWidth:1, marginBottom:8 }}>
            <Text style={{ fontWeight:'600' }}>{item.description}</Text>
            <Text>Qty: {item.qty} Ã— ${item.unitPrice} = {(parseFloat(item.qty||0)*parseFloat(item.unitPrice||0)).toFixed(2)}</Text>
          </View>
        ))}
      </ScrollView>
    </SafeAreaView>
  );
}
function ScheduleScreen(){
  const { token } = useAuth();
  const [jobs, setJobs] = useState([]);
  const [filter, setFilter] = useState('ALL'); // ALL | TODAY | WEEK
  const [queueCount, setQueueCount] = useState(0);

  const loadQueueCount = () => {
    db.transaction(tx => tx.executeSql('SELECT COUNT(*) as c FROM queue', [], (_, { rows }) => setQueueCount((rows._array?.[0]?.c)||0)));
  };

  const load = async ()=> {
    try {
      const data = await api('/jobs','GET',null,token);
      setJobs(data);
      db.transaction(tx => {
        tx.executeSql('DELETE FROM jobs_cache;');
        data.forEach(j => tx.executeSql('INSERT OR REPLACE INTO jobs_cache (id, name, status, startDate, endDate, notes) VALUES (?,?,?,?,?,?)', [j.id, j.name||'', j.status||'SCHEDULED', j.startDate||'', j.endDate||'', j.notes||'']));
      });
    } catch(e) {
      db.transaction(tx => tx.executeSql('SELECT id, name, status, startDate, endDate, notes FROM jobs_cache', [], (_, { rows }) => setJobs(rows._array || [])));
    }
  };

  useEffect(()=>{ load(); loadQueueCount(); }, [token]);

  const inRange = (d) => {
    if (!d) return false;
    const today = new Date();
    const dt = new Date(d);
    if (filter === 'TODAY') {
      return dt.getFullYear()===today.getFullYear() && dt.getMonth()===today.getMonth() && dt.getDate()===today.getDate();
    }
    if (filter === 'WEEK') {
      const start = new Date(today); start.setDate(today.getDate()-today.getDay());
      const end = new Date(start); end.setDate(start.getDate()+6);
      return dt >= start && dt <= end;
    }
    return true;
  };

  const filtered = jobs.filter(j => filter==='ALL' ? true : inRange(j.startDate));

  return (
    <SafeAreaView style={{ flex:1 }}>
      <View style={{ padding:12, borderBottomWidth:1 }}>
        <Text style={{ fontWeight:'700', marginBottom:6 }}>Schedule</Text>
        <View style={{ flexDirection:'row', gap:8, marginBottom:8 }}>
          <Button title="All" onPress={()=>setFilter('ALL')} />
          <Button title="Today" onPress={()=>setFilter('TODAY')} />
          <Button title="This Week" onPress={()=>setFilter('WEEK')} />
        </View>
        <View style={{ flexDirection:'row', justifyContent:'space-between', alignItems:'center' }}>
          <Text>Offline queue: {queueCount}</Text>
          <Button title="Sync now" onPress={async ()=>{ await processQueue(token); load(); loadQueueCount(); }} />
        </View>
      </View>
      <FlatList
        data={filtered}
        keyExtractor={j=>String(j.id)}
        contentContainerStyle={{ padding:12 }}
        renderItem={({item})=> {
          const jobsite = item.Jobsite;
          const address = jobsite ? [
            jobsite.addressLine1,
            jobsite.addressLine2,
            [jobsite.city, jobsite.state].filter(Boolean).join(', '),
            jobsite.zip,
          ].filter(Boolean).join(' - ') : '';
          return (
            <View style={{ borderWidth:1, padding:10, borderRadius:8, marginBottom:8 }}>
              <Text style={{ fontWeight:'600' }}>{item.name || `Job #${item.id}`}</Text>
              <Text>Status: {item.status}</Text>
              <Text>Date: {item.startDate || '-'}{item.endDate ? ` Ã¢â€ â€™ ${item.endDate}` : ''}</Text>
              {address ? <Text style={{ color:'#555', marginTop:4 }}>{address}</Text> : null}
              {item.notes ? <Text style={{ marginTop:4 }}>Notes: {item.notes}</Text> : null}
            </View>
          );
        }}
        ListEmptyComponent={<Text style={{ textAlign:'center', marginTop:20 }}>No jobs{filter!=='ALL' ? ' in range' : ''} - add one!</Text>}
      />
    </SafeAreaView>
  );
}

function MeScreen({ navigation }){
  const { setToken, user } = useAuth();
  return (
    <SafeAreaView style={{ flex:1, padding:16 }}>
      <Text style={{ fontSize:20, fontWeight:'600' }}>PrecisionTracker</Text>
      <Text>User: {user?.email} ({user?.role})</Text>
      {user?.role==='ADMIN' && <View style={{ height:12 }} />}
      {user?.role==='ADMIN' && <Button title="Manage Users" onPress={()=>navigation.navigate('UsersAdmin')} />}
      <View style={{ height:20 }} />
      <Button title="Sign out" onPress={()=>setToken(null)} />
    </SafeAreaView>
  );
}

const Stack = createNativeStackNavigator();
const Tabs = createBottomTabNavigator();

function TabsNav(){
  return (
    <Tabs.Navigator>
      <Tabs.Screen name="Leads" component={LeadsScreen} />
      <Tabs.Screen name="Jobs" component={JobsKanbanScreen} options={{ title:'Jobs' }} />
      <Tabs.Screen name="Schedule" component={ScheduleScreen} />
      <Tabs.Screen name="Me" component={MeScreen} />
    </Tabs.Navigator>
  );
}

function LoginScreen() {
  const [email, setEmail] = useState('admin@example.com');
  // If you kept the hardcoded backend login, default to test123:
  const [password, setPassword] = useState('test123');
  const [busy, setBusy] = useState(false);

  // <-- use the auth context to flip the app into "logged-in" mode
  const { setToken, setUser } = useAuth();

  async function login() {
    try {
      setBusy(true);
      const res = await fetch(`${API_URL}/auth/login`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password }),
      });
      const data = await res.json();
      if (!res.ok) throw new Error(data?.message || data?.error || 'Login failed');

      // Accept dev or real payload shape
      const tok = data.token || data.accessToken;
      if (!tok) throw new Error('No token returned from server');

      // ÃƒÂ¢Ã‚Â¬Ã¢â‚¬Â¡ÃƒÂ¯Ã‚Â¸Ã‚Â This is the key: store token/user in global auth state
      // Store token/user in global auth state + persist
      const normalizedUser = data.user ? { ...data.user, role: normalizeRole(data.user.role) } : { email, role: 'ADMIN', name: 'Admin' };
      setToken(tok);
      setUser(normalizedUser);
      try {
        await SecureStore.setItemAsync('auth_token', tok);
        await SecureStore.setItemAsync('auth_user', JSON.stringify(normalizedUser));
      } catch {}

      // Optional: toast
      Alert.alert('Welcome', 'Logged in');
    } catch (e) {
      Alert.alert('Error', e.message);
    } finally {
      setBusy(false);
    }
  }

  return (
      <SafeAreaView style={{ flex: 1, backgroundColor: theme.color.bg }}>
      <Screen padding={5}>
        <SectionTitle>PrecisionTracker</SectionTitle>

        <Card>
          <Text style={{ color: theme.color.text, fontSize: 16, fontWeight: '700', marginBottom: theme.space(3) }}>
            Sign in
          </Text>
          <Input placeholder="Email" autoCapitalize="none" value={email} onChangeText={setEmail} style={{ marginBottom: theme.space(2) }} />
          <Input placeholder="Password" secureTextEntry value={password} onChangeText={setPassword} style={{ marginBottom: theme.space(3) }} />
          <Button title={busy ? 'Signing in…' : 'Sign in'} onPress={login} disabled={busy} />
        </Card>
      </Screen>
    </SafeAreaView>
  );
}


export default function App(){
  const [token, setToken] = useState(null);
  const [user, setUser] = useState(null);
  const [booting, setBooting] = useState(true);

  useEffect(() => {
    let mounted = true;
    (async () => {
      try {
        const [tok, usr] = await Promise.all([
          SecureStore.getItemAsync('auth_token'),
          SecureStore.getItemAsync('auth_user')
        ]);
        if (mounted && tok) {
          setToken(tok);
          if (usr) {
            const parsed = JSON.parse(usr);
            parsed.role = normalizeRole(parsed.role);
            setUser(parsed);
          }
        }
      } catch {}
      finally { if (mounted) setBooting(false); }
    })();
    return () => { mounted = false; };
  }, []);

  const authValue = {
    token,
    setToken: (t) => {
      setToken(t);
      if (!t) { try { SecureStore.deleteItemAsync('auth_token'); SecureStore.deleteItemAsync('auth_user'); } catch {} }
    },
    user,
    setUser
  };

  if (booting) {
    return (
      <SafeAreaView style={{ flex:1, alignItems:'center', justifyContent:'center' }}>
        <Text>LoadingÃƒÂ¢Ã¢â€šÂ¬Ã‚Â¦</Text>
      </SafeAreaView>
    );
  }

  return (
    <AuthContext.Provider value={authValue}>
      <NavigationContainer>
        {!token ? (
          <Stack.Navigator>
            <Stack.Screen name="Login" component={LoginScreen} />
          </Stack.Navigator>
        ) : (
          <Stack.Navigator>
            <Stack.Screen name="Tabs" component={TabsNav} options={{ headerShown:false }} />
            <Stack.Screen name="NewLead" component={NewLeadScreen} options={{ title:'New Lead' }} />
            <Stack.Screen name="EstimateEditor" component={EstimateEditorScreen} options={{ title:'Estimate' }} />
            <Stack.Screen name="Signature" component={SignatureScreen} options={{ title:'Signature' }} />
            <Stack.Screen name="UsersAdmin" component={UsersAdminScreen} options={{ title:'Users (Admin)' }} />
            <Stack.Screen name="JobDetail" component={JobDetailScreen} options={{ title:'Job' }} />
          </Stack.Navigator>
        )}
      </NavigationContainer>
    </AuthContext.Provider>
  );
}
